import React, { Component } from 'react'
import { connect } from 'react-redux'
import { fetchArticle, searchArticle, deleteArticle } from './../actions/articleAction'
import {Container, Table, Button} from 'react-bootstrap';
import {Router, Route, Link} from 'react-router'
class ArticleList extends Component {
    componentDidMount(){
        this.props.fetchArticle()      
    }
    render() {
        return (
            <Container>
                <Table striped bordered hover>
                    <thead>
                        <tr>
                        <th>#</th>
                        <th style={{width:"20%"}}>Title</th>
                        <th style={{width:"40%"}}>Description</th>
                        <th>Thumnail</th>
                        <th>Action</th>
                        </tr>
                    </thead>
                    <tbody>
                        {this.props.articles.map(article =>     
                            <tr key={article.ID}>
                                <td>{article.ID}</td>
                                <td>{article.TITLE}</td>
                                <td>{article.DESCRIPTION}</td>
                                <td> <img src={article.IMAGE} width="80px" height="80px"/> </td>
                                <td>
                                    <Link to="/detail">
                                        <Button variant="secondary" size="sm">View</Button>
                                    </Link>
                                    <Button variant="info" size="sm" className="mx-2">Update</Button>
                                    <Button variant="danger" size="sm" 
                                        onClick = {()=>{
                                            this.props.deleteArticle(article.ID)
                                        }}
                                    >Delete</Button>
                                </td>
                            </tr>
                        )}
                    </tbody>
                    </Table>
            </Container>
        )
    }
}

//Sub to Store
const mtp = (store) => {       
    return {
        articles: store.articleR.articles
    }
}
//Connect 
export default connect (mtp, { fetchArticle, searchArticle, deleteArticle })(ArticleList)
