import React, { Component } from 'react';
import './App.css';
import ArticleList from './components/ArticleList';
import 'bootstrap/dist/css/bootstrap.min.css';
import Menu from './components/Menu';
import { BrowserRouter, Route } from 'react-router-dom';
import ViewItem from './components/ViewItem';
import AddArticle from './components/AddArticle';
import Category from './components/Category';
import About from './components/About';
import LocalizedStrings from 'localized-strings';

export const baseURL = "http://110.74.194.124:15011/";

export const strings = new LocalizedStrings({
  en:{
    home:"Home",
    category:"Category",
    about:"About",
    khmer:"Khmer",
    english:"English",
    titleApp:"Article Management System",
    title:"TITLE",
    description:"Description",
    thumbnail:"THUMBNAIL",
    action:"ACTION",
    view:"View",
    update:"Update",

  },
  kh: {
    home:"ទំព័រដើម",
    category:"ប្រភេទ",
    about:"អំពីរ",
    khmer:"ខ្មែរ",
    english:"អង់គ្លេស",
    titleApp:"ប្រពន្ធ័គ្រប់គ្រងពត័មាន",
    title:"ចំណងជើង",
    description:"ពិពណ៍នា",
    thumbnail:"រូបភាព",
    action:"សកម្មភាព"
  }},
    {/* options */}
);

export default class App extends Component {

  constructor(props){
    super(props)
    this.changeLanguage.bind(this)
  }

  changeLanguage = (lang)=>{
    strings.setLanguage(lang);
    this.setState({});
  }

  render() {
    return (
        <BrowserRouter>
        <div>
            <Menu language={this.changeLanguage}/>
            <Route exact path="/" component={ArticleList}/>  
            <Route path="/detail/:id" component={ViewItem}/>  
            <Route exact path="/addUpdate" component={AddArticle}/>  
            <Route path="/addUpdate/:id" component={AddArticle}/> 
            <Route path="/Category" component={Category}/> 
            <Route path="/About" component={About}/> 
        </div>  
      </BrowserRouter>
    )
  }
}
