import { actionType } from "../actions/actionType"

const initState = {
    articles : []
}

export const articleReducer = (state = initState, action) => {
    console.log("Type : " + action.Type);

    switch(action.Type){
        case actionType.fetch_article  : 
            console.log("Data" + action.payLoad);
            return {...state, acticles : action.payLoad}
        case actionType.search_article : 
            return {...state, acticles : action.payLoad}
        case actionType.delete_article  : 
            return {...state, acticles : state.articles.filter(article => article.ID !== action.payLoad)}
        default : return state
    }

}
